@RequestMapping("/blog")
public class BlogController {
    private final Map<Long, BlogPost> blogPosts = new HashMap<>();

    @GetMapping("/{id}")
    public BlogPost getBlogPost(@PathVariable Long id) {
        // Check if the blog post exists
        BlogPost blogPost = blogPosts.get(id);
        if (blogPost == null) {
            throw new ResourceNotFoundException();
        }

        return blogPost;
    }

    @PostMapping
    public BlogPost createBlogPost(@RequestBody BlogPost blogPost) {
        // Generate a new ID for the blog post
        Long id = blogPosts.size() + 1L;
        blogPost.setId(id);

        // Save the blog post
        blogPosts.put(id, blogPost);

        return blogPost;
    }
}

